import React, { Component } from "react"
import "./App.css"
import NavBar from "./components/NavBar"
const axios = require("axios").default
const swal = require("sweetalert2")

const URL_API = "http://localhost:3000/api"

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      titulo: "",
      descripcion: ""
    }

    this.handleChange = this.handleChange.bind(this)
    this.addTareas = this.addTareas.bind(this)
  }

  addTareas(e) {
    axios
      .post(`${URL_API}/create`, {
        titulo: this.state.titulo,
        descripcion: this.state.descripcion
      })
      .then(function (response) {
        swal.fire("Tarea agregada!", "You clicked the button!", "success")
        console.log(response)
      })
      .catch(function (error) {
        console.log(error)
      })
    e.preventDefault()
  }

  handleChange(e) {
    console.log(this.state)
    const { name, value } = e.target
    this.setState({
      [name]: value
    })
  }
  render() {
    return (
      <React.Fragment>
        <NavBar />
        <div className="col-md-6 form">
          <div className="card">
            <form onSubmit={this.addTareas} method="post">
              <div className="form-floating mb-3">
                <input
                  value={this.state.titulo}
                  onChange={this.handleChange}
                  name="titulo"
                  type="text"
                  className="form-control"
                  id="titulo-input"
                  placeholder="Titulo de la tarea"
                ></input>
                <label for="titulo-input">Titulo</label>
              </div>
              <div className="form-floating">
                <textarea
                  value={this.state.descripcion}
                  name="descripcion"
                  onChange={this.handleChange}
                  className="form-control"
                  placeholder="Descripcion de la tarea"
                  id="descripcion-input"
                ></textarea>
                <label for="descripcion-input">Descripcion</label>
              </div>
              <hr />
              <button type="submit" className="btn btn-success    ">
                Add
              </button>
            </form>
          </div>
        </div>
      </React.Fragment>
    )
  }
}

export default App
